import { useState } from "react";

function Card(props) {
  const [id, setId] = useState(null);
  return (
    <div className="card">
      {props.data.map((elm, id) => (
        <div key={id} onClick={() => setId(id)} className="card-container">
          <div className="details-container">
            <div className="card-title">{elm.title}</div>
            <div className="card-description">{elm.description}</div>
            <div className="card-description">{elm.description}</div>

            {/* <div className="card-description">
              {elm.content.replace(
                elm.content.slice(elm.content.length - 14, elm.content.length),
                ""
              )}
            </div> */}
          </div>
          <div className="author-container">
            <div className="card-author">
              <b>~{elm.author}</b>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Card;
