import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [urlData, seturlData] = useState([]);
  useEffect(() => {
    // date 
    var dateData = new Date();
    var date = dateData.getUTCDate();
    var month =
      (dateData.getMonth() + 1).toString().length <= 1
        ? "0" + (dateData.getMonth() + 1).toString()
        : (dateData.getMonth() + 1).toString();
    var year = dateData.getUTCFullYear();

    const todayDate = `${year.toString()}-${month.toString()}-${date.toString()}`;
    console.log(todayDate);
    // news url api
    const newsUrl = `https://newsapi.org/v2/everything?q=apple&from=${todayDate}&to=${todayDate}&sortBy=popularity&apiKey=23d3184ba1324c3f92d3c4c4ddafdd5f`;

    // fetch data from URl
    fetch(newsUrl)
      .then((response) => response.json())
      .then((response)=> seturlData(response))
      .then((data) => console.log(data));
  }, []);

  return <div>
    {}
  </div>;
}

export default App;
