import { useEffect, useState } from "react";

export default function Main() {
  const [urlData, seturlData] = useState([]);
  useEffect(() => {
    // date
    var dateData = new Date();
    var date = dateData.getUTCDate();
    var month =
      (dateData.getMonth() + 1).toString().length <= 1
        ? "0" + (dateData.getMonth() + 1).toString()
        : (dateData.getMonth() + 1).toString();
    var year = dateData.getUTCFullYear();

    const todayDate = `${year.toString()}-${month.toString()}-${date.toString()}`;
    // news url api
    const newsUrl = `https://newsapi.org/v2/everything?q=apple&from=${todayDate}&to=${todayDate}&sortBy=popularity&apiKey=23d3184ba1324c3f92d3c4c4ddafdd5f`;

    // fetch data from URl
    fetch(newsUrl)
      .then((response) => response.json())
      .then((response) => seturlData(response.articles));
  }, []);
  console.log("Data :", urlData);

  const mockData=[{
    author: "Mark Serrels",
content: "Remember the \"golden age\" of TV?\r\nTV's golden age most likely kicked off with The Sopranos in 1999, but it really got rolling with shows like The Wire, Lost and Deadwood in the mid-2000s. Big-time pr… [+4345 chars]",
description: "It could be the best show of 2022.",
publishedAt: "2022-08-25T01:19:00Z",
title: "Everyone Should Watch the Best TV Show on Apple TV Plus - CNET",
url: "https://www.cnet.com/culture/entertainment/everyone-should-watch-the-best-tv-show-on-apple-tv-plus/",
urlToImage: "https://www.cnet.com/a/img/resize/9b91a74f25c01f6c16cf032c16…e85ed/severance.jpg?auto=webp&fit=crop&height=630&width=1200",
  }]
  return (
    <div>
      <ul>
        {urlData.map((el) => (
          <li> {el.title} </li>
        ))}
      </ul>
    </div>
  );
}
